package kr.co.direa.basicsocket.client;

import java.io.DataOutputStream;
import java.io.IOException;
import java.net.InetSocketAddress;
import java.net.Socket;

import org.springframework.stereotype.Component;

import jakarta.annotation.PostConstruct;
import lombok.AllArgsConstructor;
import lombok.NoArgsConstructor;
import lombok.Setter;
import lombok.extern.slf4j.Slf4j;

@Slf4j
@NoArgsConstructor
@AllArgsConstructor
@Setter
@Component
public class PlainTextEchoClient {
	private String remoteIp;
	private int remotePort;

	@PostConstruct
	public void init() {
		Socket socket = null;
		DataOutputStream outToServer = null;
		byte[] data = "".getBytes();

		try {
			InetSocketAddress inetSocketAddress = new InetSocketAddress(remoteIp, remotePort);

			socket = new Socket();
			socket.setKeepAlive(true);
			socket.setReuseAddress(true);
			socket.setTcpNoDelay(true);
			socket.setSoTimeout(15000);
			socket.connect(inetSocketAddress, 30000);

			log.info("1) Async CMD [" + inetSocketAddress + "] Connected..");

			outToServer = new DataOutputStream(socket.getOutputStream());

			outToServer.write(data);
			outToServer.flush();
			log.info("2) Async ChannelCheckTgrm Send (Length " + data.length + ")");
		} catch (IOException e) {
			log.error("", e);
		} finally {
			// try {
			// 	Thread.sleep(2000);
			// } catch (InterruptedException ignored) {
			// }

			try {
				if (outToServer != null) {
					outToServer.close();
					log.info("3) OutputStream Closed..");
				}
			} catch (Exception ignored) {
			}
			try {
				if (socket != null) {
					socket.close();
					log.info("4) Socket Closed..");
				}
			} catch (Exception ignored) {
			}
			outToServer = null;
			socket = null;
		}
	}
}